const Product = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
  };

  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      const response = await fetch(`http://localhost:8080/workshop/${id}`);
      const data = await response.json();
      setProduct(data.data[0]);
      setLoading(false);
      
      // Fetch tickets for the workshop
      const ticketsResponse = await fetch(`http://localhost:8080/ticket/getAllTicketsByIdWorkshop/${id}`);
      const ticketsData = await ticketsResponse.json();
      setTickets(ticketsData.data);
    };
    getProduct();
  }, [id]);

  const Loading = () => {
    return (
      <>
        {/* Your loading skeleton component */}
      </>
    );
  };

  const ShowProduct = () => {
    return (
      <>
        <div className="container my-5 py-2">
          <div className="row">
            <div className="col-md-6 col-sm-12 py-3">
              {/* Render product information */}
            </div>
            <div className="col-md-6 col-md-6 py-5">
              {/* Render product information */}
            </div>
          </div>
        </div>

        {/* Render ticket list */}
        <div class="portlet-body">
          <table class="table">
            <thead>
              <tr class="active">
                <th scope="col">Ticket Name</th>
                <th scope="col">Description</th>
                <th scope="col">Price</th>
              </tr>
            </thead>
            <tbody>
              {tickets.map((ticket, index) => (
                <tr key={index}>
                  <td>{ticket.nameTicket}</td>
                  <td>{ticket.descriptionTicket}</td>
                  <td>${ticket.priceTicket}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </>
    );
  };

  return (
    <>
      {/* Your Header component */}
      <Header />
      <div className="container">
        <div className="row">
          {loading ? <Loading /> : <ShowProduct />}
        </div>
        {/* Your Footer component */}
        <Footer />
      </div>
    </>
  );
};

export default Product;
