import React, { useState, useEffect } from "react";
import { Footer, Header } from "../components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Lấy idUser từ cookie
    const idUser = document.cookie.replace(
      /(?:(?:^|.*;\s*)idUser\s*=\s*([^;]*).*$)|^.*$/,
      "$1"
    );

    // Nếu có idUser, gọi API để lấy thông tin giỏ hàng
    if (idUser) {
      fetchCart(idUser);
    } else {
      setLoading(false);
    }
  }, []);

  // Hàm gọi API để lấy thông tin giỏ hàng
  const fetchCart = async (idUser) => {
    try {
      const response = await fetch(`http://localhost:8080/cart/${idUser}`);
      if (response.ok) {
        const data = await response.json();
        setCartItems(data.data);
      } else {
        console.error("Failed to fetch cart items");
      }
    } catch (error) {
      console.error("Error fetching cart items:", error);
    }
    setLoading(false);
  };

  const removeItem = (itemToRemove) => {
    // Xóa sản phẩm khỏi giỏ hàng
    const updatedCart = cartItems.filter((item) => item.id !== itemToRemove.id);
    setCartItems(updatedCart);
  };

  const deleteItem = async (itemToRemove) => {
    // Gọi API để xóa sản phẩm khỏi giỏ hàng trên server
    // Sau khi xóa thành công, cập nhật lại state của giỏ hàng
    try {
      // Gọi API để xóa sản phẩm
      const response = await fetch(
        `http://localhost:8080/cart/${itemToRemove.id}`,
        {
          method: "DELETE",
        }
      );
      if (response.ok) {
        removeItem(itemToRemove);
      } else {
        console.error("Failed to delete item from cart");
      }
    } catch (error) {
      console.error("Error deleting item from cart:", error);
    }
  };

  // Hàm thêm sản phẩm vào giỏ hàng
  const addItem = (itemToAdd) => {
    // Thêm sản phẩm vào giỏ hàng
    // Cập nhật state của giỏ hàng
  };

  return (
    <>
      <Header />
      <div className="container my-3 py-3">
        <h1 className="text-center">Cart</h1>
        <hr />
        {loading ? (
          <p>Loading...</p>
        ) : cartItems.length > 0 ? (
          <ShowCart
            cartItems={cartItems}
            removeItem={removeItem}
            deleteItem={deleteItem}
            addItem={addItem} // Truyền hàm addItem xuống component con ShowCart
          />
        ) : (
          <EmptyCart />
        )}
      </div>
      <Footer />
    </>
  );
};

const EmptyCart = () => {
  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12 py-5 bg-light text-center">
          <h4 className="p-3 display-5">Your Cart is Empty</h4>
          <Link to="/" className="btn btn-outline-dark mx-4">
            <i className="fa fa-arrow-left"></i> Continue Shopping
          </Link>
        </div>
      </div>
    </div>
  );
};

const ShowCart = ({ cartItems, removeItem, deleteItem, addItem }) => {
  let subtotal = 0;
  let totalItems = 0;

  return (
    <section className="h-100 gradient-custom">
      <div className="container py-5">
        <div className="row d-flex justify-content-center my-4">
          <div className="col-md-8">
            <div className="card mb-4">
              <div className="card-header py-3">
                <h5 className="mb-0">Item List</h5>
              </div>
              <div className="card-body">
                {cartItems.map((item, index) => {
                  subtotal += item.priceTicket * item.quantityTicket;
                  totalItems += item.quantityTicket;

                  return (
                    <div key={index}>
                      <div className="row d-flex align-items-center">
                        <div className="col-lg-3 col-md-12">
                          <div
                            className="bg-image rounded"
                            data-mdb-ripple-color="light"
                          >
                            <img
                              src={item.imageWorkshop}
                              alt={item.nameWorkshop}
                              width={100}
                              height={75}
                            />
                          </div>
                        </div>
                        <div className="col-lg-5 col-md-6">
                          <p>
                            <strong>{item.nameWorkshop}</strong>
                          </p>
                        </div>
                        <div className="col-lg-4 col-md-6">
                          <div
                            className="d-flex mb-4"
                            style={{ maxWidth: "300px" }}
                          >
                            <button
                              className="btn px-3"
                              onClick={() => removeItem(item)}
                            >
                              <i className="fas fa-minus"></i>
                            </button>
                            <p className="mx-5">{item.quantityTicket}</p>
                            <button
                              className="btn px-3"
                              onClick={() => addItem(item)}
                            >
                              <i className="fas fa-plus"></i>
                            </button>
                          </div>
                          <p className="text-start text-md-center">
                            <strong>
                              <span className="text-muted">
                                {item.quantityTicket}
                              </span>{" "}
                              x đ{item.priceTicket}
                            </strong>
                          </p>
                        </div>
                      </div>
                      <hr className="my-4" />
                      <button
                        className="btn btn-danger"
                        onClick={() => deleteItem(item)}
                      >
                        <FontAwesomeIcon icon={faTrash} /> Remove
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
        <div className="text-center">
          <h5>Total items: {totalItems}</h5>
          <h5>Subtotal: đ{subtotal}</h5>
          <Link to="/" className="btn btn-outline-dark mx-4">
            <i className="fa fa-arrow-left"></i> Continue Shopping
          </Link>
        </div>
      </div>
    </section>
  );
};

export default Cart;
