import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";

import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";

import { Link } from "react-router-dom";

const Products = () => {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState([]);
  const [loading, setLoading] = useState(false);
  let componentMounted = true;

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
  };

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);
      const response = await fetch("http://localhost:8080/workshop");
      if (componentMounted) {
        const responseData = await response.json();
        // Thêm các thuộc tính nameCompanyWorkshop, imageCompanyWorkshop, và descriptionCompanyWorkshop
        const updatedData = responseData.map(item => ({
          ...item,
          nameCompanyWorkshop: item.nameCompanyWorkshop,
          imageCompanyWorkshop: item.imageCompanyWorkshop,
          descriptionCompanyWorkshop: item.descriptionCompanyWorkshop
        }));
        setData(updatedData);
        setFilter(updatedData); // Thay vì gán lại filter từ dữ liệu mới, hãy giữ nguyên filter là một mảng rỗng ban đầu
        setLoading(false);
      }
      return () => {
        componentMounted = false;
      };
    };
    getProducts();
  }, []);

  const Loading = () => {
    return (
      <>
        <div className="col-12 py-5 text-center">
          <Skeleton height={40} width={560} />
        </div>
        <div className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
          <Skeleton height={592} />
        </div>
        <div className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
          <Skeleton height={592} />
        </div>
        <div className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
          <Skeleton height={592} />
        </div>
        <div className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
          <Skeleton height={592} />
        </div>
        <div className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
          <Skeleton height={592} />
        </div>
        <div className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
          <Skeleton height={592} />
        </div>
      </>
    );
  };

  const filterWorkshop = (cat) => {
    const updatedList = data.filter((item) => item.nameCategoryWorkshop === cat);
    setFilter(updatedList); // Sử dụng danh sách đã lọc để cập nhật filter
  };
  
  
  const ShowWorkshops = () => {
    return (
      <>
        <div className="buttons text-center py-5">
          <button className="btn btn-outline-dark btn-sm m-2" onClick={() => setFilter([...data])}>
            1
          </button>
          <button className="btn btn-outline-dark btn-sm m-2" onClick={() => filterWorkshop("men's clothing")}>
            2
          </button>
          <button className="btn btn-outline-dark btn-sm m-2" onClick={() => filterWorkshop("women's clothing")}>
            5
          </button>
          <button className="btn btn-outline-dark btn-sm m-2" onClick={() => filterWorkshop("jewelery")}>
            3
          </button>
          <button className="btn btn-outline-dark btn-sm m-2" onClick={() => filterWorkshop("electronics")}>
            4
          </button>
        </div>
  
        {Array.isArray(filter) && filter.map((workshop) => (
          <div id={workshop.idWorkshop} key={workshop.idWorkshop} className="col-md-4 col-sm-6 col-xs-8 col-12 mb-4">
            <div className="card text-center h-100" key={workshop.idWorkshop}>
              <img className="card-img-top p-3" src={workshop.imageWorkshop} alt="Card" height={300} />
              <div className="card-body">
                <h5 className="card-title">{workshop.nameWorkshop}</h5>
                <p className="card-text">{workshop.descriptionWorkshop}</p>
              </div>
              <ul className="list-group list-group-flush">
                <li className="list-group-item lead">$ {workshop.timeWorkshop}</li>
              </ul>
              <div className="card-body">
                <Link to={"/product/" + workshop.idWorkshop} className="btn btn-dark m-1">
                  Buy Now
                </Link>
                <button className="btn btn-dark m-1" onClick={() => addProduct(workshop)}>
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </>
    );
  };
  

  return (
    <div className="container my-3 py-3">
      <div className="row">
        <div className="col-12">
          <h2 className="display-5 text-center">Latest Workshops</h2>
          <hr />
        </div>
      </div>
      <div className="row justify-content-center">
        {loading ? <Loading /> : <ShowWorkshops />}
      </div>
    </div>
  );
};

export default Products;
