import React, { useEffect, useState } from "react";
import { Footer, Header } from "../components";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import Cookies from "js-cookie";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCartItems = async () => {
      const userId = Cookies.get("userId");
      if (!userId) {
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`http://localhost:8080/cart/${userId}`);
        const data = await response.json();
        if (data.statusCode === 200) {
          setCartItems(data.data);
        }
      } catch (error) {
        console.error("Error fetching cart items:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  const EmptyCart = () => (
    <div className="container">
      <div className="row">
        <div className="col-md-12 py-5 bg-light text-center">
          <h4 className="p-3 display-5">Your Cart is Empty</h4>
          <Link to="/" className="btn btn-outline-dark mx-4">
            <i className="fa fa-arrow-left"></i> Continue Shopping
          </Link>
        </div>
      </div>
    </div>
  );

  const deleteItem = async (idCart) => {
    try {
      const response = await fetch(`http://localhost:8080/cart/${idCart}`, {
        method: "DELETE",
      });
      if (response.ok) {
        setCartItems(cartItems.filter((item) => item.idCart !== idCart));
      } else {
        console.error("Failed to delete cart item");
      }
    } catch (error) {
      console.error("Error deleting cart item:", error);
    }
  };

  const ShowCart = () => {
    let subtotal = 0;
    let shipping = 30.0;
    let totalItems = 0;

    cartItems.forEach((item) => {
      subtotal += item.totalPrice;
      totalItems += item.quantityProduct + item.quantityTicket;
    });

    return (
      <>
        <section className="h-100 gradient-custom">
          <div className="container py-5">
            <div className="row d-flex justify-content-center my-4">
              <div className="col-md-8">
                <div className="card mb-4">
                  <div className="card-header py-3">
                    <h5 className="mb-0">Item List</h5>
                  </div>
                  <div className="card-body">
                    {cartItems.map((item) => (
                      <div key={item.idCart}>
                        <div className="row d-flex align-items-center">
                          <div className="col-lg-3 col-md-12">
                            <div
                              className="bg-image rounded"
                              data-mdb-ripple-color="light"
                            >
                              <img
                                src={`http://localhost:8080/images/${item.imageWorkshop
                                  .split("\\")
                                  .pop()}`}
                                alt={item.nameWorkshop}
                                width={100}
                                height={75}
                              />
                            </div>
                          </div>
                          <div className="col-lg-5 col-md-6">
                            <p>
                              <strong>{item.nameWorkshop}</strong>
                            </p>
                          </div>
                          <div className="col-lg-4 col-md-6">
                            <p className="text-start text-md-center">
                              <strong>
                                <span className="text-muted">
                                  {item.quantityProduct + item.quantityTicket}
                                </span>{" "}
                                x {item.priceProduct + item.priceTicket} đ
                              </strong>
                            </p>
                          </div>
                        </div>
                        <hr className="my-4" />
                        <button
                          className="btn btn-danger"
                          onClick={() => deleteItem(item.idCart)}
                        >
                          <FontAwesomeIcon icon={faTrash} /> Remove
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card mb-4">
                  <div className="card-header py-3 bg-light">
                    <h5 className="mb-0">Order Summary</h5>
                  </div>
                  <div className="card-body">
                    <ul className="list-group list-group-flush">
                      <li className="list-group-item d-flex justify-content-between align-items-center border-0 px-0 pb-0">
                        Products ({totalItems})
                        <span>${Math.round(subtotal)}</span>
                      </li>
                      <li className="list-group-item d-flex justify-content-between align-items-center px-0">
                        Shipping<span>${shipping}</span>
                      </li>
                      <li className="list-group-item d-flex justify-content-between align-items-center border-0 px-0 mb-3">
                        <div>
                          <strong>Total amount</strong>
                        </div>
                        <span>
                          <strong>${Math.round(subtotal + shipping)}</strong>
                        </span>
                      </li>
                    </ul>
                    <Link
                      to="/checkout"
                      className="btn btn-dark btn-lg btn-block"
                    >
                      Go to checkout
                    </Link>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </>
    );
  };

  return (
    <>
      <Header />
      <div className="container my-3 py-3">
        <h1 className="text-center">Cart</h1>
        <hr />
        {loading ? (
          <div>Loading...</div>
        ) : cartItems.length > 0 ? (
          <ShowCart />
        ) : (
          <EmptyCart />
        )}
      </div>
      <Footer />
    </>
  );
};

export default Cart;
