import React, { useEffect, useState } from "react";
import Skeleton from "react-loading-skeleton";
import { Link, useParams } from "react-router-dom";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";
import { Footer, Header } from "../components";

const Product = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
  };

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const productResponse = await fetch(`http://localhost:8080/workshop/${id}`);
      const productData = await productResponse.json();
      setProduct(productData.data[0]);
      
      const ticketResponse = await fetch(`http://localhost:8080/ticket/getAllTicketsByIdWorkshop/${id}`);
      const ticketData = await ticketResponse.json();
      setTickets(ticketData.data);
      
      setLoading(false);
    };

    fetchData();
  }, [id]);

  const Loading = () => {
    return (
      <>
        <div className="container my-5 py-2">
          <div className="row">
            {/* Placeholder skeleton loading UI */}
          </div>
        </div>
      </>
    );
  };

  const ShowProduct = () => {
    return (
      <>
        <div className="container my-5 py-2">
          <div className="row">
            {/* Product details */}
          </div>
          <div className="row">
            <div className="col-md-8">
              <h4>Ticket List</h4>
              <table className="table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {tickets.map((ticket, index) => (
                    <tr key={index}>
                      <td>{ticket.nameTicket}</td>
                      <td>{ticket.descriptionTicket}</td>
                      <td>{ticket.priceTicket}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </>
    );
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="row">{loading ? <Loading /> : <ShowProduct />}</div>
        <Footer />
      </div>
    </>
  );
};

export default Product;
