import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

export default function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();

  useEffect(() => {
    // Kiểm tra cookie "authenticated" để xác định trạng thái đăng nhập của người dùng
    const isAuthenticated = document.cookie.includes("authenticated=true");
    setIsLoggedIn(isAuthenticated);
  }, [location]);

  const handleLogout = async () => {
    try {
      const response = await fetch("http://localhost:8080/user/logout");
      const data = await response.json();
      if (response.ok) {
        // Xóa cookie thành công, cập nhật trạng thái đăng nhập
        setIsLoggedIn(false);
        alert(data.message);
      } else {
        console.error("Đăng xuất không thành công:", data.error);
      }
    } catch (error) {
      console.error("Lỗi khi thực hiện đăng xuất:", error);
    }
  };

  return (
    <header className="header">
      <div className="container-fluid">
        <div className="row">
          {/* Code khác không thay đổi */}

          <div className="col-lg-3">
            <div className="header__right">
              <ul className="header__right__widget">
                {/* Hiển thị liên kết "Login" và "Register" nếu chưa đăng nhập */}
                {!isLoggedIn && (
                  <div className="header__right__auth">
                    <Link to="/Login" className="nav-link">
                      Login
                    </Link>
                    <Link to="/Register" className="nav-link">
                      Register
                    </Link>
                  </div>
                )}

                {/* Hiển thị avatar và liên kết "Logout" nếu đã đăng nhập */}
                {isLoggedIn && (
                  <li className="nav-item dropdown">
                    <a
                      className="nav-link dropdown-toggle d-flex align-items-center"
                      href="#"
                      id="navbarDropdownMenuLink"
                      role="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                    >
                      <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/Avatars/img (31).webp"
                        className="rounded-circle"
                        height="22"
                        alt="Avatar"
                        loading="lazy"
                      />
                    </a>

                    <ul
                      className="dropdown-menu"
                      aria-labelledby="navbarDropdownMenuLink"
                    >
                      <li>
                        <Link className="dropdown-item" to="/myprofile">
                          My profile
                        </Link>
                      </li>
                      <li>
                        <Link className="dropdown-item" to="/myhistory">
                          History
                        </Link>
                      </li>
                      <li>
                        {/* Gọi hàm handleLogout khi người dùng nhấp vào "Logout" */}
                        <button
                          className="dropdown-item"
                          onClick={handleLogout}
                        >
                          Logout
                        </button>
                      </li>
                    </ul>
                  </li>
                )}
              </ul>
            </div>
          </div>
        </div>
        <div className="canvas__open">
          <i className="fa fa-bars"></i>
        </div>
      </div>
    </header>
  );
}
