import React, { useEffect, useState } from "react";
import Skeleton from "react-loading-skeleton";
import { Link, useParams } from "react-router-dom";
import Marquee from "react-fast-marquee";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";

import { Footer, Header } from "../components";

const Product = () => {
  const { id } = useParams();
  const [product, setProduct] = useState([]);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loading2, setLoading2] = useState(false);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
  };

  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      setLoading2(true);
      const response = await fetch(`http://localhost:8080/workshop/${id}`);
      const data = await response.json();
      setProduct(data.data[0]);
      setLoading(false);
      setLoading2(false);
    };
    getProduct();
  }, [id]);

  const Loading = () => {
    return (
      <>
        <div className="container my-5 py-2">
          <div className="row">
            <div className="col-md-6 py-3">
              <Skeleton height={400} width={400} />
            </div>
            <div className="col-md-6 py-5">
              <Skeleton height={30} width={250} />
              <Skeleton height={90} />
              <Skeleton height={40} width={70} />
              <Skeleton height={50} width={110} />
              <Skeleton height={120} />
              <Skeleton height={40} width={110} inline={true} />
              <Skeleton className="mx-3" height={40} width={110} />
            </div>
          </div>
        </div>
      </>
    );
  };

  const CompanyInfo = ({ name, description, image }) => {
    return (
      <div>
        <h4>Company Information:</h4>
        <p><strong>Name:</strong> {name}</p>
        <p><strong>Description:</strong> {description}</p>
        {image && (
          <img
            className="img-fluid"
            src={`http://localhost:8080/images/${image.split("\\").pop()}`}
            alt={name}
            width="200px"
            height="200px"
          />
        )}
      </div>
    );
  };
  
  const ShowProduct = () => {
    return (
      <>
        <div className="container my-5 py-2">
          <div className="row">
            <div className="col-md-6 col-sm-12 py-3">
              <img
                className="img-fluid"
                src={product.imageWorkshop && `http://localhost:8080/images/${product.imageWorkshop.split("\\").pop()}`}
                alt={product.nameWorkshop}
                width="400px"
                height="400px"
              />
            </div>
            <div className="col-md-6 col-md-6 py-5">
              <h4 className="text-uppercase text-muted">{product.nameCategoryWorkshop}</h4>
              <h1 className="display-5">{product.nameWorkshop}</h1>
              <p className="lead">
                {product.addressWorkshop}{""}
              </p>
              <h3 className="display-6  my-4">{product.timeWorkshop}</h3>
              <p className="lead">{product.descriptionWorkshop}</p>
              
              <button
                className="btn btn-outline-dark"
                onClick={() => addProduct(product)}
              >
                Add to Cart
              </button>
              <Link to="/cart" className="btn btn-dark mx-3">
                Go to Cart
              </Link>
  
              <CompanyInfo
                name={product.nameCompanyWorkshop}
                description={product.descriptionCompanyWorkshop}
                image={product.imageCompanyWorkshop}
              />
            </div>
          </div>
        </div>
      </>
    );
  };
  
  

  return (
    <>
      <Header/>
      <div className="container">
        <div className="row">{loading ? <Loading /> : <ShowProduct />}</div>
        <Footer />
      </div>
    </>
  );
};

export default Product;
