import React, { useEffect, useState } from "react";
import { Footer, Header } from "../components";
import { useDispatch } from "react-redux";
import { delCart } from "../redux/action";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    // Truy xuất id user từ cookie ở đây và gọi API lấy thông tin cart dựa trên id user
    const fetchCart = async () => {
      try {
        const response = await fetch(
          `http://localhost:8080/cart/{id_user_from_cookie}`
        );
        const data = await response.json();
        if (response.ok) {
          setCartItems(data.data); // Set state với thông tin cart nhận được từ API
        } else {
          console.error("Failed to fetch cart items:", data.message);
        }
      } catch (error) {
        console.error("Error fetching cart items:", error);
      }
    };

    fetchCart();
  }, []); // Chỉ gọi API lấy cart khi component mount lần đầu

  const removeItem = (product) => {
    dispatch(delCart(product));
  };

  const deleteItem = (product) => {
    dispatch(delCart(product));
  };

  return (
    <>
      <Header />
      <div className="container my-3 py-3">
        <h1 className="text-center">Cart</h1>
        <hr />
        {cartItems.length > 0 ? (
          <div className="row">
            <div className="col-md-8">
              <div className="card mb-4">
                <div className="card-header py-3">
                  <h5 className="mb-0">Item List</h5>
                </div>
                <div className="card-body">
                  {cartItems.map((item) => (
                    <div key={item.id}>
                      {/* Hiển thị thông tin sản phẩm trong cart */}
                    </div>
                  ))}
                </div>
              </div>
            </div>
            <div className="col-md-4">{/* Hiển thị Order Summary */}</div>
          </div>
        ) : (
          <div className="row">
            <div className="col-md-12 py-5 bg-light text-center">
              <h4 className="p-3 display-5">Your Cart is Empty</h4>
              <Link to="/" className="btn btn-outline-dark mx-4">
                <i className="fa fa-arrow-left"></i> Continue Shopping
              </Link>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Cart;
