import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State lưu trữ trạng thái đăng nhập
  const [username, setUsername] = useState(""); // State lưu trữ tên người dùng

  // Hàm xử lý đăng nhập thành công
  const handleLoginSuccess = (username) => {
    // Thực hiện logic đăng nhập
    setIsLoggedIn(true); // Cập nhật trạng thái đăng nhập
    setUsername(username); // Lưu tên người dùng
  };

  // Hàm xử lý đăng xuất
  const handleLogout = () => {
    // Thực hiện logic đăng xuất
    setIsLoggedIn(false); // Cập nhật trạng thái đăng nhập
    setUsername(""); // Xóa tên người dùng
  };

  useEffect(() => {
    // Kiểm tra xem người dùng đã đăng nhập hay chưa khi component được render
    const userLoggedIn = localStorage.getItem("isLoggedIn");
    const storedUsername = localStorage.getItem("username");
    if (userLoggedIn === "true" && storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }
  }, []);

  return (
    <header className="header">
      <div className="container-fluid">
        <div className="row">
          <div className="col-xl-3 col-lg-2">
            <div className="header__logo">
              <img src="/img/open.png" alt="logo" />
            </div>
          </div>
          <div className="col-xl-6 col-lg-7">
            <nav className="header__menu">
              {/* Menu */}
            </nav>
          </div>
          <div className="col-lg-3">
            <div className="header__right">
              {isLoggedIn ? (
                // Hiển thị khi đã đăng nhập
                <ul className="header__right__widget">
                  {/* Avatar và các action khác */}
                  {/* Điều chỉnh dựa vào trạng thái đăng nhập */}
                  <li className="nav-item dropdown">
                    {/* Avatar */}
                    <a
                      className="nav-link dropdown-toggle d-flex align-items-center"
                      href="#"
                      id="navbarDropdownMenuLink"
                      role="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                    >
                      <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/Avatars/img (31).webp"
                        className="rounded-circle"
                        height="22"
                        alt="Avatar"
                        loading="lazy"
                      />
                      <span>{username}</span>
                    </a>

                    {/* Dropdown menu */}
                    <ul
                      className="dropdown-menu"
                      aria-labelledby="navbarDropdownMenuLink"
                    >
                      <li>
                        <Link className="dropdown-item" to="/myprofile">
                          My profile
                        </Link>
                      </li>
                      <li>
                        <Link className="dropdown-item" to="/myhistory">
                          History
                        </Link>
                      </li>
                      <li>
                        <button className="dropdown-item" onClick={handleLogout}>
                          Logout
                        </button>
                      </li>
                    </ul>
                  </li>
                </ul>
              ) : (
                // Hiển thị khi chưa đăng nhập
                <div className="header__right__auth">
                  <Link to="/Login" className="nav-link">
                    Login
                  </Link>
                  <Link to="/Register" className="nav-link">
                    Register
                  </Link>
                </div>
              )}
            </div>
          </div>
        </div>
        <div className="canvas__open">
          <i className="fa fa-bars"></i>
        </div>
      </div>
    </header>
  );
}
