import React, { useEffect, useState } from "react";
import Skeleton from "react-loading-skeleton";
import { Link, useParams } from "react-router-dom";
import Marquee from "react-fast-marquee";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";

import { Footer, Header } from "../components";

const Product = () => {
  const { id } = useParams();
  const [workshop, setWorkshop] = useState({});
  const [similarWorkshops, setSimilarWorkshops] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loading2, setLoading2] = useState(false);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
  };

  useEffect(() => {
    const getWorkshop = async () => {
      setLoading(true);
      setLoading2(true);
      const response = await fetch(`http://localhost:8080/workshop/${id}`);
      const data = await response.json();
      setWorkshop(data.data[0]);
      setLoading(false);
      const response2 = await fetch(
        `https://fakestoreapi.com/products/category/${data.category}`
      );
      const data2 = await response2.json();
      setSimilarWorkshops(data2);
      setLoading2(false);
    };
    getWorkshop();
  }, [id]);

  const Loading = () => {
    return (
      <>
        <div className="container my-5 py-2">
          <div className="row">
            <div className="col-md-6 py-3">
              <Skeleton height={400} width={400} />
            </div>
            <div className="col-md-6 py-5">
              <Skeleton height={30} width={250} />
              <Skeleton height={90} />
              <Skeleton height={40} width={70} />
              <Skeleton height={50} width={110} />
              <Skeleton height={120} />
              <Skeleton height={40} width={110} inline={true} />
              <Skeleton className="mx-3" height={40} width={110} />
            </div>
          </div>
        </div>
      </>
    );
  };

  const ShowWorkshop = () => {
    return (
      <>
        <div className="container my-5 py-2">
          <div className="row">
            <div className="col-md-6 col-sm-12 py-3">
              <img
                className="img-fluid"
                src={`http://localhost:8080/images/${workshop.imageWorkshop.split("\\").pop()}`}
                alt={workshop.nameWorkshop}
                width="400px"
                height="400px"
              />
            </div>
            <div className="col-md-6 col-md-6 py-5">
              <h4 className="text-uppercase text-muted">{workshop.nameCategoryWorkshop}</h4>
              <h1 className="display-5">{workshop.nameWorkshop}</h1>
              <p className="lead">
                {workshop.timeWorkshop} <i className="fa fa-clock-o"></i>
              </p>
              <h3 className="display-6  my-4">{workshop.descriptionWorkshop}</h3>
              <button
                className="btn btn-outline-dark"
                onClick={() => addProduct(workshop)}
              >
                Add to Cart
              </button>
              <Link to="/cart" className="btn btn-dark mx-3">
                Go to Cart
              </Link>
            </div>
          </div>
        </div>
      </>
    );
  };

  const Loading2 = () => {
    return (
      <>
        <div className="my-4 py-4">
          <div className="d-flex">
            <div className="mx-4">
              <Skeleton height={400} width={250} />
            </div>
            <div className="mx-4">
              <Skeleton height={400} width={250} />
            </div>
            <div className="mx-4">
              <Skeleton height={400} width={250} />
            </div>
            <div className="mx-4">
              <Skeleton height={400} width={250} />
            </div>
          </div>
        </div>
      </>
    );
  };

  const ShowSimilarWorkshops = () => {
    return (
      <>
        <div className="py-4 my-4">
          <div className="d-flex">
            {similarWorkshops.map((workshop) => {
              return (
                <div key={workshop.id} className="card mx-4 text-center">
                  <img
                    className="card-img-top p-3"
                    src={workshop.image}
                    alt="Card"
                    height={300}
                    width={300}
                  />
                  <div className="card-body">
                    <h5 className="card-title">
                      {workshop.nameWorkshop.substring(0, 15)}...
                    </h5>
                  </div>
                  <div className="card-body">
                    <Link
                      to={"/workshop/" + workshop.idWorkshop}
                      className="btn btn-dark m-1"
                    >
                      View Workshop
                    </Link>
                    <button
                      className="btn btn-dark m-1"
                      onClick={() => addProduct(workshop)}
                    >
                      Add to Cart
                    </button>
                  </div>
                </div>
              );
           
            })}
            </div>
          </div>
        </>
      );
    };
  
    return (
      <>
        <Header />
        <div className="container">
          <div className="row">
            {loading ? <Loading /> : <ShowWorkshop />}
          </div>
          <div className="row my-5 py-5">
            <div className="d-none d-md-block">
              <h2 className="">Similar Workshops</h2>
              <Marquee pauseOnHover={true} pauseOnClick={true} speed={50}>
                {loading2 ? <Loading2 /> : <ShowSimilarWorkshops />}
              </Marquee>
            </div>
          </div>
        </div>
        <Footer />
      </>
    );
  };
  
  export default Product;
