import React, { useEffect, useState } from "react";
import { Footer, Header } from "../components";
import { useSelector, useDispatch } from "react-redux";
import { addCart, delCart } from "../redux/action";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";

const Cart = () => {
  const [cartData, setCartData] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    // Lấy ID người dùng từ cookie
    const userId = getUserIdFromCookie(); // Hàm này bạn cần tự viết để trích xuất ID người dùng từ cookie

    // Gọi API để lấy giỏ hàng của người dùng
    fetch(`http://localhost:8080/cart/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data && data.data) {
          setCartData(data.data);
        }
      })
      .catch((error) => console.error("Error fetching cart data:", error));
  }, []);

  const getUserIdFromCookie = () => {
    // Viết hàm này để trích xuất ID người dùng từ cookie
    // Trả về ID người dùng từ cookie
  };

  const addItem = (product) => {
    dispatch(addCart(product));
  };

  const removeItem = (product) => {
    dispatch(delCart(product));
  };

  const deleteItem = (product) => {
    dispatch(delCart(product));
  };

  const EmptyCart = () => {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-12 py-5 bg-light text-center">
            <h4 className="p-3 display-5">Your Cart is Empty</h4>
            <Link to="/" className="btn btn-outline-dark mx-4">
              <i className="fa fa-arrow-left"></i> Continue Shopping
            </Link>
          </div>
        </div>
      </div>
    );
  };

  const ShowCart = () => {
    // Code hiển thị giỏ hàng tại đây dựa trên cartData
  };

  return (
    <>
      <Header />
      <div className="container my-3 py-3">
        <h1 className="text-center">Cart</h1>
        <hr />
        {cartData.length > 0 ? <ShowCart /> : <EmptyCart />}
      </div>
      <Footer />
    </>
  );
};

export default Cart;
