import React, { useState, useEffect } from "react";
import { Footer, Header } from "../components";
import { useSelector, useDispatch } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();

  // Lấy userId từ cookie
  useEffect(() => {
    const userId = parseInt(
      document.cookie.replace(
        /(?:(?:^|.*;\s*)userId\s*=\s*([^;]*).*$)|^.*$/,
        "$1"
      )
    );

    // Gọi API để lấy giỏ hàng của người dùng
    const fetchCart = async () => {
      try {
        const response = await fetch(`http://localhost:8080/cart/${userId}`);
        if (response.ok) {
          const data = await response.json();
          setCartItems(data.data); // Lưu thông tin giỏ hàng vào state
        } else {
          console.error("Failed to fetch cart data");
        }
        setLoading(false);
      } catch (error) {
        console.error("Error fetching cart data:", error);
        setLoading(false);
      }
    };

    fetchCart();
  }, []);

  // Xóa một mặt hàng khỏi giỏ hàng
  const deleteItem = async (idCart) => {
    try {
      const response = await fetch(`http://localhost:8080/cart/${idCart}`, {
        method: "DELETE",
      });
      if (response.ok) {
        setCartItems(cartItems.filter((item) => item.idCart !== idCart));
      } else {
        console.error("Failed to delete item from cart");
      }
    } catch (error) {
      console.error("Error deleting item from cart:", error);
    }
  };

  // Hiển thị giỏ hàng
  const showCartItems = () => {
    if (loading) {
      return <p>Loading...</p>;
    } else if (cartItems.length === 0) {
      return (
        <div className="container">
          <div className="row">
            <div className="col-md-12 py-5 bg-light text-center">
              <h4 className="p-3 display-5">Your Cart is Empty</h4>
              <Link to="/" className="btn btn-outline-dark mx-4">
                <i className="fa fa-arrow-left"></i> Continue Shopping
              </Link>
            </div>
          </div>
        </div>
      );
    } else {
      return (
        <div className="container">
          <div className="row">
            <div className="col-md-12 py-5 bg-light text-center">
              <h4 className="p-3 display-5">Your Cart Items</h4>
              {cartItems.map((item) => (
                <div key={item.idCart} className="row mb-3">
                  <div className="col-md-4">
                    <img
                      src={item.imageWorkshop}
                      alt={item.nameWorkshop}
                      className="img-fluid"
                    />
                  </div>
                  <div className="col-md-8">
                    <h5>{item.nameWorkshop}</h5>
                    <p>Quantity: {item.quantityTicket}</p>
                    <p>Price: ${item.priceTicket}</p>
                    <button
                      className="btn btn-danger"
                      onClick={() => deleteItem(item.idCart)}
                    >
                      <FontAwesomeIcon icon={faTrash} /> Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      );
    }
  };

  return (
    <>
      <Header />
      {showCartItems()}
      <Footer />
    </>
  );
};

export default Cart;
