import React, { useEffect, useState } from "react";
import Skeleton from "react-loading-skeleton";
import { Link, useParams } from "react-router-dom";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action"; // Thêm import cho hàm addCart
import { Footer, Header } from "../components";

const Product = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();

  const addProduct = (productId) => {
    // Sửa hàm addProduct để truyền id của sản phẩm
    dispatch(addCart(productId)); // Thay vì truyền đối tượng sản phẩm, chỉ truyền id
  };

  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      const response = await fetch(`http://localhost:8080/workshop/${id}`);
      const data = await response.json();
      setProduct(data.data[0]);
      setLoading(false);
    };
    getProduct();
  }, [id]);

  const Loading = () => (
    <div className="container my-5 py-2">
      <div className="row">
        <div className="col-md-6 py-3">
          <Skeleton height={400} width={400} />
        </div>
        <div className="col-md-6 py-5">
          <Skeleton height={30} width={250} />
          <Skeleton height={90} />
          <Skeleton height={40} width={70} />
          <Skeleton height={50} width={110} />
          <Skeleton height={120} />
          <Skeleton height={40} width={110} inline={true} />
          <Skeleton className="mx-3" height={40} width={110} />
        </div>
      </div>
    </div>
  );

  const ShowProduct = () => (
    <div className="container my-5 py-2">
      <div className="row">
        <div className="col-md-6 col-sm-12 py-3">
          <img
            className="img-fluid"
            src={
              product.imageWorkshop &&
              `http://localhost:8080/images/${product.imageWorkshop
                .split("\\")
                .pop()}`
            }
            alt={product.nameWorkshop}
            width="400px"
            height="400px"
          />
        </div>
        <div className="col-md-6 col-md-6 py-5">
          <h4 className="text-uppercase text-muted">
            {product.nameCategoryWorkshop}
          </h4>
          <h1 className="display-5">{product.nameWorkshop}</h1>
          <p className="lead">{product.addressWorkshop}</p>
          <h3 className="display-6 my-4">{product.timeWorkshop}</h3>
          <p className="lead">{product.descriptionWorkshop}</p>
          <button
            className="btn btn-outline-dark"
            onClick={() => addProduct(product.id)} // Sử dụng id của sản phẩm khi nhấn nút Add to Cart
          >
            Add to Cart
          </button>
          <Link to="/cart" className="btn btn-dark mx-3">
            Go to Cart
          </Link>
        </div>

        {/* Thêm phần hiển thị thông tin vé và nút Add to Cart cho vé */}
        {/*...*/}

        <div>
          <br />
          <h4>Ban tổ chức</h4>

          <div className="d-flex align-items-center">
            {product.imageCompanyWorkshop && (
              <img
                className="img-fluid"
                src={`http://localhost:8080/images/${product.imageCompanyWorkshop
                  .split("\\")
                  .pop()}`}
                alt={product.nameCompanyWorkshop}
                width="200px"
                height="200px"
              />
            )}
            <div className="ms-3">
              <p>
                <strong>{product.nameCompanyWorkshop}</strong>
              </p>
              <p>{product.descriptionCompanyWorkshop}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const getUserIdFromCookie = () => {
    // Viết hàm để lấy id của người dùng từ cookie ở đây
    //...
  };

  const addProductToCart = async () => {
    try {
      const userId = getUserIdFromCookie(); // Lấy id của người dùng từ cookie
      if (!userId) {
        console.error("User ID not found.");
        return;
      }

      const requestData = {
        quantityProduct: 1, // Số lượng sản phẩm không thay đổi
        quantityTicket: 1, // Số lượng vé không thay đổi
        idUser: userId,
        idProduct: product.id, // ID của sản phẩm
        idTicket: product.ticketId, // ID của vé
      };

      const response = await fetch("http://localhost:8080/cart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestData),
      });

      if (response.ok) {
        // Nếu thêm vào giỏ hàng thành công, dispatch action để cập nhật store Redux
        dispatch(addCart(product.id));
        alert("Item added to cart successfully!");
      } else {
        console.error("Failed to add item to cart.");
        alert("Failed to add item to cart. Please try again later.");
      }
    } catch (error) {
      console.error("Error adding item to cart:", error);
      alert("Failed to add item to cart. Please try again later.");
    }
  };

  return (
    <>
      <Header />
      <div className="container">
        {loading ? <Loading /> : <ShowProduct />}
        <Footer />
      </div>
    </>
  );
};

export default Product;
