import React, { useState, useEffect } from "react";
import { Footer, Header } from "../components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { addCart, delCart } from "../redux/action";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const idUser = getUserId(); // Lấy idUser từ cookie hoặc trạng thái đăng nhập
        if (!idUser) {
          console.error("User ID not found.");
          return;
        }

        const response = await fetch(`http://localhost:8080/cart/${idUser}`);
        if (response.ok) {
          const data = await response.json();
          setCartItems(data.data);
        } else {
          console.error("Failed to fetch cart items");
        }
      } catch (error) {
        console.error("Error fetching cart items:", error);
      }
    };

    fetchCart();
  }, []);

  const addItem = (product) => {
    dispatch(addCart(product));
  };

  const removeItem = (product) => {
    dispatch(delCart(product));
  };

  const deleteItem = (product) => {
    dispatch(delCart(product));
  };

  // Lấy idUser từ cookie hoặc trạng thái đăng nhập
  const getUserId = () => {
    const cookies = document.cookie.split(";").map((cookie) => cookie.trim());
    const userIdCookie = cookies.find((cookie) => cookie.startsWith("userId="));
    if (userIdCookie) {
      return userIdCookie.split("=")[1];
    } else {
      return null;
    }
  };
  return (
    <>
      <Header />
      <div className="container my-3 py-3">
        <h1 className="text-center">Cart</h1>
        <hr />
        {cartItems.length > 0 ? (
          <section className="h-100 gradient-custom">
            <div className="container py-5">
              <div className="row d-flex justify-content-center my-4">
                <div className="col-md-8">
                  <div className="card mb-4">
                    <div className="card-header py-3">
                      <h5 className="mb-0">Item List</h5>
                    </div>
                    <div className="card-body">
                      {cartItems.map((item, index) => (
                        <div key={item.id}>
                          <div className="row d-flex align-items-center">
                            <div className="col-lg-3 col-md-12">
                              <div className="bg-image rounded">
                                <img
                                  src={`http://localhost:8080/images/${item.imageWorkshop
                                    .split("\\")
                                    .pop()}`}
                                  alt={item.nameWorkshop}
                                  width={100}
                                  height={75}
                                />
                              </div>
                              <br />
                              <button
                                className="btn btn-danger"
                                onClick={() => deleteItem(item)}
                                style={{
                                  backgroundColor: "white",
                                  color: "black",
                                  border: "1px solid black",
                                }}
                              >
                                <FontAwesomeIcon icon={faTrash} /> Remove
                              </button>
                            </div>

                            <div className="col-lg-5 col-md-6">
                              <p>
                                <strong>{item.nameWorkshop}</strong>
                              </p>
                            </div>
                            <div className="col-lg-4 col-md-6">
                              <div
                                className="d-flex mb-4 align-items-center"
                                style={{ maxWidth: "300px" }}
                              >
                                <button
                                  className="btn px-3"
                                  onClick={() => removeItem(item)}
                                >
                                  <i className="fas fa-minus"></i>
                                </button>

                                <p className="mx-3">{item.quantityTicket}</p>
                                <button
                                  className="btn px-3"
                                  onClick={() => addItem(item)}
                                >
                                  <i className="fas fa-plus"></i>
                                </button>
                              </div>

                              <p className="text-start text-md-center">
                                <strong>
                                  <span className="text-muted">
                                    {item.quantityTicket}
                                  </span>{" "}
                                  x {item.priceTicket} đ
                                </strong>
                              </p>
                            </div>
                          </div>
                          <hr className="my-4" />
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="card mb-4">
                    <div className="card-header py-3 bg-light">
                      <h5 className="mb-0">Order Summary</h5>
                    </div>
                    <div className="card-body">
                      <ul className="list-group list-group-flush">
                        <li className="list-group-item d-flex justify-content-between align-items-center border-0 px-0 pb-0">
                          Products ({cartItems.length}) <br />
                        </li>

                        <li className="list-group-item d-flex justify-content-between align-items-center border-0 px-0 mb-3">
                          <div>
                            <strong>Total amount</strong>
                          </div>
                          <span>
                            <strong>
                              ${" "}
                              {Math.round(
                                cartItems.reduce((acc, cur) => {
                                  if (!isNaN(cur.totalPrice)) {
                                    return acc + cur.totalPrice;
                                  } else {
                                    return acc;
                                  }
                                }, 0)
                              )}
                            </strong>
                          </span>
                        </li>
                      </ul>
                      <Link
                        to="/checkout"
                        className="btn btn-dark btn-lg btn-block"
                      >
                        Go to checkout
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
        ) : (
          <div className="container">
            <div className="row">
              <div className="col-md-12 py-5 bg-light text-center">
                <h4 className="p-3 display-5">Your Cart is Empty</h4>
                <Link to="/" className="btn btn-outline-dark mx-4">
                  <i className="fa fa-arrow-left"></i> Continue Shopping
                </Link>
              </div>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Cart;
