import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function Header() {
  const [loggedIn, setLoggedIn] = useState(true); // State để kiểm tra trạng thái đăng nhập

  const handleLogout = async () => {
    try {
      const response = await fetch("http://localhost:8080/user/logout", {
        method: "GET",
        credentials: "include",
      });
      if (response.ok) {
        setLoggedIn(false); // Sau khi logout thành công, cập nhật trạng thái đăng nhập
      }
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  return (
    <header className="header">
      <div className="container-fluid">
        <div className="row">
          {/* Code cho phần logo và menu */}
          {/* ... */}
          {/* Code cho phần đăng nhập và đăng ký */}
          <div className="col-lg-3">
            <div className="header__right">
              <ul className="header__right__widget">
                {/* Hiển thị các liên kết chỉ khi chưa đăng nhập hoặc đã đăng xuất */}
                {!loggedIn && (
                  <div className="header__right__auth">
                    <Link to="/Login" className="nav-link">
                      Login
                    </Link>
                    <Link to="/Register" className="nav-link">
                      Register
                    </Link>
                  </div>
                )}

                {/* Hiển thị thông tin người dùng và nút logout khi đã đăng nhập */}
                {loggedIn && (
                  <li className="nav-item dropdown">
                    <a
                      className="nav-link dropdown-toggle d-flex align-items-center"
                      href="#"
                      id="navbarDropdownMenuLink"
                      role="button"
                      data-bs-toggle="dropdown"
                      aria-expanded="false"
                    >
                      <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/Avatars/img (31).webp"
                        className="rounded-circle"
                        height="22"
                        alt="Avatar"
                        loading="lazy"
                      />
                    </a>
                    <ul
                      className="dropdown-menu"
                      aria-labelledby="navbarDropdownMenuLink"
                    >
                      <li>
                        <Link className="dropdown-item" to="/myprofile">
                          My profile
                        </Link>
                      </li>
                      <li>
                        <Link className="dropdown-item" to="/myhistory">
                          History
                        </Link>
                      </li>
                      <li>
                        <button
                          className="dropdown-item"
                          onClick={handleLogout} // Gọi hàm handleLogout khi người dùng click vào nút logout
                        >
                          Logout
                        </button>
                      </li>
                    </ul>
                  </li>
                )}
              </ul>
            </div>
          </div>
        </div>
        {/* Code cho phần mở rộng menu */}
        {/* ... */}
      </div>
    </header>
  );
}
