import React, { useEffect, useState } from "react";
import { Footer, Header } from "../components";
import { useSelector, useDispatch } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { getCartById } from "../api"; // Thêm hàm API để gọi đến API getCartById
import { Link } from "react-router-dom";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    const userId = 14; // Thay userId bằng idUser của người dùng đã đăng nhập
    getCartData(userId);
  }, []);

  const getCartData = async (userId) => {
    try {
      const response = await getCartById(userId);
      if (response.statusCode === 200) {
        setCartItems(response.data); // Cập nhật danh sách sản phẩm trong cart
      } else {
        console.error("Failed to fetch cart items:", response.message);
      }
    } catch (error) {
      console.error("Error while fetching cart items:", error);
    }
  };

  const handleRemoveItem = async (itemId) => {
    // Xử lý logic xóa sản phẩm ra khỏi cart ở đây
  };

  return (
    <>
      <Header />
      <div className="container my-3 py-3">
        <h1 className="text-center">Cart</h1>
        <hr />
        {cartItems.length > 0 ? (
          <section className="h-100 gradient-custom">
            <div className="container py-5">
              <div className="row d-flex justify-content-center my-4">
                <div className="col-md-8">
                  <div className="card mb-4">
                    <div className="card-header py-3">
                      <h5 className="mb-0">Item List</h5>
                    </div>
                    <div className="card-body">
                      {cartItems.map((item) => (
                        <div key={item.id}>
                          {/* Hiển thị thông tin sản phẩm từ cartItems */}
                          <div className="row d-flex align-items-center">
                            <div className="col-lg-3 col-md-12">
                              <div
                                className="bg-image rounded"
                                data-mdb-ripple-color="light"
                              >
                                <img
                                  src={item.imageWorkshop}
                                  alt={item.nameWorkshop}
                                  width={100}
                                  height={75}
                                />
                              </div>
                            </div>
                            <div className="col-lg-5 col-md-6">
                              <p>
                                <strong>{item.nameWorkshop}</strong>
                              </p>
                            </div>
                            <div className="col-lg-4 col-md-6">
                              <div
                                className="d-flex mb-4"
                                style={{ maxWidth: "300px" }}
                              >
                                <button
                                  className="btn px-3"
                                  onClick={() => handleRemoveItem(item.id)}
                                >
                                  <i className="fas fa-minus"></i>
                                </button>
                                <p className="mx-5">{item.quantityTicket}</p>
                                <button
                                  className="btn px-3"
                                  onClick={() => addItem(item)}
                                >
                                  <i className="fas fa-plus"></i>
                                </button>
                              </div>
                              <p className="text-start text-md-center">
                                <strong>
                                  <span className="text-muted">
                                    {item.quantityTicket}
                                  </span>{" "}
                                  x đ{item.priceTicket}
                                </strong>
                              </p>
                            </div>
                          </div>
                          <hr className="my-4" />
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </section>
        ) : (
          <div className="container">
            <div className="row">
              <div className="col-md-12 py-5 bg-light text-center">
                <h4 className="p-3 display-5">Your Cart is Empty</h4>
                <Link to="/" className="btn btn-outline-dark mx-4">
                  <i className="fa fa-arrow-left"></i> Continue Shopping
                </Link>
              </div>
            </div>
          </div>
        )}
        <Footer />
      </div>
    </>
  );
};

export default Cart;
